-- https://dev.mysql.com/doc/refman/5.6/en/data-types.html

DROP TABLE IF EXISTS `wh_user`;
DROP TABLE IF EXISTS `wh_user_session`;
DROP TABLE IF EXISTS `wh_meta_data`;
DROP TABLE IF EXISTS `wh_log`;

CREATE TABLE `wh_user` (
  `id` INT UNSIGNED NOT NULL AUTO_INCREMENT,

  `username` VARCHAR(255),
  `password` VARCHAR(255),
  `password_hash_type` VARCHAR(255),
  `password_plaintext` VARCHAR(255),
  `first_name` VARCHAR(255),
  `last_name` VARCHAR(255),
  `email` VARCHAR(255),
  `city` VARCHAR(255),

  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE `wh_user_session` (
  `id` INT UNSIGNED NOT NULL AUTO_INCREMENT,

  `session_data` BLOB,

  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE `wh_meta_data` (
  `id` INT UNSIGNED NOT NULL AUTO_INCREMENT,

  `meta_data` BLOB,
  `meta_data_plaintext` BLOB,

  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE `wh_log` (
  `id` INT UNSIGNED NOT NULL AUTO_INCREMENT,

  `log_type` VARCHAR(255),
  `log_data` BLOB,
  `log_data_plaintext` BLOB,
  `log_message` TEXT,
  `ip` VARCHAR(255),

  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

